CREATE TABLE USER (EMAIL VARCHAR(50) NOT NULL, NAME VARCHAR(50) NOT NULL, PASSWORD VARCHAR(50) NOT NULL, PRIMARY KEY (EMAIL))
CREATE TABLE INGREDIENT (NAME VARCHAR(50) NOT NULL, ABV INT, DESCRIPTION TEXT, IMAGE TEXT, PRIMARY KEY (NAME))
CREATE TABLE INGREDIENTMEASURES (MEASURE VARCHAR(50), COCKTAIL_NAME VARCHAR(50) NOT NULL, INGREDIENT_NAME VARCHAR(50) NOT NULL, PRIMARY KEY (COCKTAIL_NAME, INGREDIENT_NAME))
CREATE TABLE COCKTAIL (NAME VARCHAR(50) NOT NULL, ALCOHOLIC BOOLEAN, IMAGE TEXT, INSTRUCTIONS TEXT, PRIMARY KEY (NAME))
CREATE TABLE barshelf (User_EMAIL VARCHAR(50) NOT NULL, barshelf_NAME VARCHAR(50) NOT NULL, PRIMARY KEY (User_EMAIL, barshelf_NAME))
CREATE TABLE wishlist (User_EMAIL VARCHAR(50) NOT NULL, wishlist_NAME VARCHAR(50) NOT NULL, PRIMARY KEY (User_EMAIL, wishlist_NAME))
ALTER TABLE INGREDIENTMEASURES ADD CONSTRAINT FK_INGREDIENTMEASURES_INGREDIENT_NAME FOREIGN KEY (INGREDIENT_NAME) REFERENCES INGREDIENT (NAME)
ALTER TABLE INGREDIENTMEASURES ADD CONSTRAINT FK_INGREDIENTMEASURES_COCKTAIL_NAME FOREIGN KEY (COCKTAIL_NAME) REFERENCES COCKTAIL (NAME)
ALTER TABLE barshelf ADD CONSTRAINT FK_barshelf_User_EMAIL FOREIGN KEY (User_EMAIL) REFERENCES USER (EMAIL)
ALTER TABLE barshelf ADD CONSTRAINT FK_barshelf_barshelf_NAME FOREIGN KEY (barshelf_NAME) REFERENCES INGREDIENT (NAME)
ALTER TABLE wishlist ADD CONSTRAINT FK_wishlist_User_EMAIL FOREIGN KEY (User_EMAIL) REFERENCES USER (EMAIL)
ALTER TABLE wishlist ADD CONSTRAINT FK_wishlist_wishlist_NAME FOREIGN KEY (wishlist_NAME) REFERENCES INGREDIENT (NAME)
